mapper.select:
SELECT g.id, g.headline, g.conent, u.username, DATE_FORMAT(g.create_time,'%Y-%m-%d %H:%i:%S') create_time, f.accessory_address, f.accessory_name, f.fk_id, f.accessory_suffix FROM sys_message g LEFT JOIN ( SELECT a.accessory_name, a.accessory_address, a.accessory_suffix, a.fk_id FROM sys_accessory a GROUP BY a.fk_id ) f ON g.id = f.fk_id LEFT JOIN sys_user u on u.user_id=g.create_user WHERE 1=1
and g.headline =#{headline}
order by g.create_user desc
